import React, { useState } from 'react';
import TextField from "@material-ui/core/TextField";
import { Button} from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import ReactDOM from 'react-dom';
var Link = require('react-router').Link;
// import { BrowserRouter as Router, Switch, RouteوRedirect } from 'react-router-dom';





var appointment ="";
var globalVisaType="";





function Fnc() {
  
    return (
  




        
      <div style={{
                      
                          
                          
                      
        textAlign: 'center',    
        marginTop: "15%",
            
          }}>
  
          <h1>شما {appointment} هستید</h1>
  
    
  
          {(() => {
  
    
  
             switch (appointment) {
  
                case 'وکیل':


        

return (
    <div>
                          <h1>ورود به پنل وکالت
                          </h1>
                    
                          <a href="http://localhost:3000/lawyer-app" rel="noreferrer">
    برای ورود به پنل وکالت کلیک کنید.
  </a>
  

</div>
         );              
                    
                    
                case 'منشی':
  
              
return (
  <div>
                        <h1>ورود به پنل منشی
                        </h1>
                  
                        <a href="http://localhost:3000/clerk-app" rel="noreferrer">
  برای ورود به پنل منشی کلیک کنید.
</a>


</div>
       ); 
                default:
  
                  
                    return(
                      
                      <div
                      style={{
                       
                        display: 'flex',
                        justifyContent: 'Center',
                        alignItems: 'Center',
                        height: '100vh',
                       
                        
                        
                      }}
                    >
                      
                     <div 
                     style={{
                      
                          
                          
                      
                  textAlign: 'center',    
                    
                      
                    }}
                  >
                  
                  
                  
                  
                  
                  
                      
                      
                  <h2 >موکل گرامی بعد از چند روز وکیلتان با شما ارتباط برقرار خواهد کرد . با تشکر<br></br>
  
  نوع مهاجرت انتخابی : {globalVisaType}</h2>
                    
                    
                    
                  
                    </div>
                    </div>
                    )
                  
  
             }
  
    
  
          })()}
  
    
  
      </div>
  
    );
          
  }


    
//   return(
    
//     <div
//     style={{
     
//       display: 'flex',
//       justifyContent: 'Center',
//       alignItems: 'Center',
//       height: '100vh',
            
//     }}
//   >
    
//    <div 
//    style={{
      
// textAlign: 'center',    
  
//   }}
// >
  
//     <h2 >موکل گرامی یعد از چند روز وکیلتان با شما ارتبازط برقرار خواهد کرد . با تشکر<br></br>
  
//   نوع مهاجرت انتخابی : {appointment}</h2>
  
//   </div>
//   </div>
//   );





const SignUp = () => {
  const [name, setName] = useState("");
  const [pass, setPass] = useState("");
  const [mobile, setMobile] = useState("");
  const [value, setValue] = useState("");
  const [position, setPosition] = useState("");

  const getInitialState = () => {
    const value = "موکل";
    
    return value;
    
  };



  
  const handleChange = (e) => {
    setValue(e.target.value);
    
  };
  return (
    <div
    style={{
     
      display: 'flex',
      justifyContent: 'Center',
      alignItems: 'Center',
      height: '100vh',
      backgroundImage: `url(/airplane-flight.jpg)`,
      backgroundSize: 'cover', 
   
    }}
  >
    
   <div 
   style={{
    
        
textAlign: 'center',    
  color: "lightgrey"
    
  }}
>


      <h2>ثبت نام</h2>
      <TextField
    
        value={name}
        label="نام کاربری"
        onChange={(e) => {
          setName(e.target.value);
        }}
      />
      <h3>نام کاربری: {name} </h3>


      
      <TextField
   
        value={pass}
        label="رمز"
        onChange={(e) => {
          setPass(e.target.value);
        }}
      />
      <h3>رمز: {pass} </h3>


      <TextField
  
        value={mobile}
        label="موبایل"
        onChange={(e) => {
          setMobile(e.target.value);
        }}
      />
      <h3>موبایل: {mobile} </h3>

      <TextField
  
        value={value}
        label="نوع ویزا"
        onChange={(e) => {
          setValue(e.target.value);
        }}
      />
      <h3>نوع ویزا: {value} </h3>



      
    
<TextField

        value={position}
        label="نقش"
        onChange={(e) => {
          setPosition(e.target.value);
        }}
      />
      <h3>نقش: {position} </h3>


      
      <Button

      
  onClick={() => {
    // const txt = `پرداخت موفقیت آمیز بود .نوبت شما : ${value}`;
    // alert(txt);
    
    
    appointment = position;
   globalVisaType=value;
    ReactDOM.render(<Fnc />, document.getElementById('root'));
  }}

  to='/homePage' activeStyle
>
  تایید
</Button>





    </div>
    </div>
  );
};
  
export default SignUp;
