<Link className="btn btn-primary" 
  style={{marginTop: '30px',display: 'block'}}
  onClick={() => {
    postdata()
    appointment = value;
    Fnc()
  }}>تایید</Link>

    </div>
    </div>
  );
};
  
export default SignIn;
